chunkingPhrases:
{
  "topic": "Project updates",
  "scenarios": [
    {
      "scenario": "Introducing a new project",
      "questions": [
        "What is the main goal of the new project?",
        "Who are the key stakeholders involved in this project?",
        "What challenges do you anticipate in the early stages?",
        "How do you plan to measure the success of this project?"
      ]
    },
    {
      "scenario": "Weekly progress report",
      "questions": [
        "What milestones have been achieved this week?",
        "Can you describe any obstacles faced during the week?",
        "How does this week's progress compare to the initial timeline?",
        "What are the next steps for the upcoming week?"
      ]
    },
    {
      "scenario": "Discussing technical issues",
      "questions": [
        "What specific technical challenges are we currently facing?",
        "How have these issues impacted the project timeline?",
        "What resources do we need to resolve these technical problems?",
        "Can you share any previous experiences with similar issues?"
      ]
    },
    {
      "scenario": "Proposing improvement solutions",
      "questions": [
        "What improvements do you think could enhance our workflow?",
        "How would you prioritize these proposed solutions?",
        "What potential risks should we consider with these changes?",
        "Can you provide examples of successful improvements from other projects?"
      ]
    },
    {
      "scenario": "Feedback from the development team",
      "questions": [
        "What feedback have you received from the team about the project?",
        "How do you think this feedback can influence our next steps?",
        "Are there any recurring themes in the feedback that we should address?",
        "What suggestions do you have for improving team communication?"
      ]
    }
  ]
}
